SELECT dosen.kd_dosen AS `code Dosen`, CONCAT_WS(' ', dosen.f_name, dosen.m_name, dosen.l_name) AS `Nama Dosen`, mk.title AS `Mata Kuliah`,  mahasiswa.nim AS `NIM Mahasiswa`, CONCAT_WS(' ', mahasiswa.f_name,mahasiswa.m_name,mahasiswa.l_name) AS `Nama Mahasiswa` FROM	dosen AS dosen INNER JOIN matkul AS mk ON dosen.kd_matkul = mk.id INNER JOIN mahasiswa AS mahasiswa	ON mk.id = mahasiswa.kd_matkul